"use strict";(self.webpackChunkstudyu_docs=self.webpackChunkstudyu_docs||[]).push([[725],{9574:e=>{e.exports=JSON.parse('{"blogPosts":[{"id":"federal-health-minister-meets-studyu","metadata":{"permalink":"/de/blog/federal-health-minister-meets-studyu","source":"@site/blog/2023-06-8-presentation-to-health-minister.mdx","title":"Introducing StudyU to the German Federal Minister of Health","description":"During his visit to the Digital Health Cluster of the Hasso Plattner Institute (HPI) on June 8, 2023,","date":"2023-06-08T00:00:00.000Z","formattedDate":"8. Juni 2023","tags":[],"readingTime":0.82,"hasTruncateMarker":false,"authors":[],"frontMatter":{"slug":"federal-health-minister-meets-studyu","title":"Introducing StudyU to the German Federal Minister of Health"},"nextItem":{"title":"StudyU has won the Best Idea Award 2022 at HPI","permalink":"/de/blog/best-idea-award"}},"content":"import Image from \'@theme/IdealImage\';\\nimport thumbnail from \'/img/health_minister.jpg\';\\nimport CaptionWrapper from \\"@site/src/components/CaptionWrapper\\";\\n\\nDuring his visit to the Digital Health Cluster of the Hasso Plattner Institute (HPI) on June 8, 2023, \\nwe had the privilege of introducing StudyU to the German Federal Minister of Health,\\nProf. Dr. Karl Lauterbach. The Prime Minister of Health was already aware about the potential of N-of-1 trials \\nand how they can provide personalized insights into treatment effectiveness. Through his visit, \\nProf. Dr. Lauterbach was able to gain firsthand knowledge about how StudyU offers an empowering opportunity \\nto develop individualized care plans and enhance patient outcomes.\\n\\n<CaptionWrapper caption=\\"Dr. Stefan Konigorski showcasing the capabilities of StudyU for personalized\\nhealthcare interventions to the German Federal Minister of Health, Prof. Dr. Karl Lauterbach\\">\\n<Image img={thumbnail} alt=\\"\\"/>\\n</CaptionWrapper>\\n\\nWe want to thank Prof. Dr. Karl Lauterbach for taking his time to visit the Digital Health Cluster at HPI\\nto learn more about StudyU and other ongoing digital health research projects.\\n\\nImage credits: Leon Stebe"},{"id":"best-idea-award","metadata":{"permalink":"/de/blog/best-idea-award","source":"@site/blog/2022-10-17-best-idea-award.mdx","title":"StudyU has won the Best Idea Award 2022 at HPI","description":"We are honored to announce that StudyU has won the Best Idea Award 2022 of the School Of Entrepreneurship at","date":"2022-10-17T00:00:00.000Z","formattedDate":"17. Oktober 2022","tags":[],"readingTime":0.76,"hasTruncateMarker":false,"authors":[],"frontMatter":{"slug":"best-idea-award","title":"StudyU has won the Best Idea Award 2022 at HPI"},"prevItem":{"title":"Introducing StudyU to the German Federal Minister of Health","permalink":"/de/blog/federal-health-minister-meets-studyu"}},"content":"import Image from \'@theme/IdealImage\';\\nimport thumbnail from \'/img/best_idea_award.png\';\\nimport CaptionWrapper from \\"@site/src/components/CaptionWrapper\\";\\n\\nWe are honored to announce that StudyU has won the <b>Best Idea Award 2022</b> of the School Of Entrepreneurship at\\nHasso Plattner Institute (HPI).\\n\\nWinning this award grants us the opportunity to <b>visit HPI New York City</b> and meet its director,\\nDr. Joann Halpern. Additionally, it enables us to establish connections with HPI\uff65MS, a collaborative\\nproject between HPI and [Mount Sinai Health System](https://www.mountsinai.org/), renowned as one of the world\'s top-ranked hospitals.\\n\\nWith these new possibilities, our goal is to further enhance the development of StudyU, so that a\\ngreater number of individuals will be able to experience the advantages of personalized healthcare interventions.\\n\\nMany thanks to everyone who voted for us!\\n\\nRead more on the HPI website: [https://hpi.de/entrepreneurship/best-idea.html](https://hpi.de/entrepreneurship/best-idea.html).\\n\\n<CaptionWrapper caption=\\"The winning team StudyU with Pauline Gieseler und Johannes Vedder (middle)\\">\\n<Image img={thumbnail} alt=\\"\\"/>\\n</CaptionWrapper>\\n\\nImage credits: HPI School Of Entrepreneurship"}]}')}}]);